        total = h * 60 + m
        total %= 24 * 60
        self.h = total // 60
        self.m = total % 60

    def __repr__(self):
        return f"{self.h:02d}:{self.m:02d}"

    def __add__(self, minute):
        if not isinstance(minute, int):
            raise TypeError("Vrijeme se mo≈æe zbrajati samo s brojem minuta (int)!")
        total = (self.h * 60 + self.m + minute) % (24 * 60)
        return Vrijeme(total // 60, total % 60)

    def __sub__(self, minute):
        if not isinstance(minute, int):
            raise TypeError("Vrijeme se mo≈æe oduzimati samo s brojem minuta (int)!")
        total = (self.h * 60 + self.m - minute) % (24 * 60)
        return Vrijeme(total // 60, total % 60)

    def __iadd__(self, minute):
        if not isinstance(minute, int):
            raise TypeError("Vrijeme se mo≈æe uveƒáati samo za broj minuta (int)!")
        total = (self.h * 60 + self.m + minute) % (24 * 60)
        self.h, self.m = total // 60, total % 60
        return self

    def __isub__(self, minute):
        if not isinstance(minute, int):
            raise TypeError("Vrijeme se mo≈æe smanjiti samo za broj minuta (int)!")
        total = (self.h * 60 + self.m - minute) % (24 * 60)
        self.h, self.m = total // 60, total % 60
        return self

