#include <Ultrasonic.h>

// Define the ultrasonic sensor pins
const uint8_t ultrasonic_sensor_HC_SR04_Echo_PIN_D3 = 3;
const uint8_t ultrasonic_sensor_HC_SR04_Trigger_PIN_D2 = 2;

