namespace LINQ_03
{
    internal class Program
    {
        static void Main(string[] args)
        {
            Hospital hospital = new Hospital();
            Menu menu = new Menu(hospital);

            menu.Run();
        }
    }

    public class Patient
    {
        public Patient(string lastName, string firstName, string parentName, int age, string disease)
        {
            LastName = lastName;
            FirstName = firstName;
            Patronymic = parentName;
            Age = age;
            Disease = disease;
        }

        public string LastName { get; }
        public string FirstName { get; }
        public string Patronymic { get; }
        public int Age { get; }
        public string Disease { get; }

        public override string ToString()
        {
            return $"–§–ò–û: {LastName} {FirstName} {Patronymic}, –í–æ–∑—Ä–∞—Å—Ç: {Age}, –ó–∞–±–æ–ª–µ–≤–∞–Ω–∏–µ: {Disease}";
        }
    }

    public class Hospital
    {
        private List<Patient> _patients = new List<Patient>()
        {
            new Patient("–ò–≤–∞–Ω–æ–≤", "–°–µ—Ä–≥–µ–π", "–ê–ª–µ–∫—Å–∞–Ω–¥—Ä–æ–≤–∏—á", 35, "–ì—Ä–∏–ø–ø"),
            new Patient("–°–º–∏—Ä–Ω–æ–≤–∞", "–ï–ª–µ–Ω–∞", "–í–∏–∫—Ç–æ—Ä–æ–≤–Ω–∞", 28, "–û—Å—Ç—Ä—ã–π –±—Ä–æ–Ω—Ö–∏—Ç"),
            new Patient("–ö—É–∑–Ω–µ—Ü–æ–≤", "–î–º–∏—Ç—Ä–∏–π", "–ò–≥–æ—Ä–µ–≤–∏—á", 52, "–ü—Ä–æ—Å—Ç—É–¥–∞"),
            new Patient("–ü–æ–ø–æ–≤–∞", "–ê–Ω–Ω–∞", "–°–µ—Ä–≥–µ–µ–≤–Ω–∞", 19, "–í–µ—Ç—Ä—è–Ω–∞—è –æ—Å–ø–∞"),
            new Patient("–í–∞—Å–∏–ª—å–µ–≤", "–ê–Ω–¥—Ä–µ–π", "–û–ª–µ–≥–æ–≤–∏—á", 67, "–•—Ä–æ–Ω–∏—á–µ—Å–∫–∞—è —Å–µ—Ä–¥–µ—á–Ω–∞—è –Ω–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ—Å—Ç—å"),
            new Patient("–°–æ–∫–æ–ª–æ–≤–∞", "–¢–∞—Ç—å—è–Ω–∞", "–ù–∏–∫–æ–ª–∞–µ–≤–Ω–∞", 45, "–ú–∏–≥—Ä–µ–Ω—å"),
            new Patient("–ú–∏—Ö–∞–π–ª–æ–≤", "–ò–ª—å—è", "–ü–µ—Ç—Ä–æ–≤–∏—á", 12, "–ê–ª–ª–µ—Ä–≥–∏—á–µ—Å–∫–∏–π —Ä–∏–Ω–∏—Ç"),
            new Patient("–ù–æ–≤–∏–∫–æ–≤–∞", "–û–ª—å–≥–∞", "–ì–µ–Ω–Ω–∞–¥—å–µ–≤–Ω–∞", 31, "–ü—Ä–æ—Å—Ç—É–¥–∞"),
            new Patient("–§–µ–¥–æ—Ä–æ–≤", "–ú–∞–∫—Å–∏–º", "–í–ª–∞–¥–∏–º–∏—Ä–æ–≤–∏—á", 74, "–°–∞—Ö–∞—Ä–Ω—ã–π –¥–∏–∞–±–µ—Ç 2 —Ç–∏–ø–∞"),
            new Patient("–ú–æ—Ä–æ–∑–æ–≤–∞", "–Æ–ª–∏—è", "–ü–∞–≤–ª–æ–≤–Ω–∞", 22, "–ì—Ä–∏–ø–ø"),
            new Patient("–í–æ–ª–∫–æ–≤", "–ê–ª–µ–∫—Å–µ–π", "–ï–≤–≥–µ–Ω—å–µ–≤–∏—á", 49, "–û—Å—Ç–µ–æ—Ö–æ–Ω–¥—Ä–æ–∑ –ø–æ—è—Å–Ω–∏—á–Ω–æ–≥–æ –æ—Ç–¥–µ–ª–∞"),
            new Patient("–õ–µ–±–µ–¥–µ–≤–∞", "–ù–∞—Ç–∞–ª—å—è", "–î–º–∏—Ç—Ä–∏–µ–≤–Ω–∞", 38, "–ê–Ω–µ–º–∏—è"),
            new Patient("–ö–æ–∑–ª–æ–≤", "–†–æ–º–∞–Ω", "–ê—Ä–∫–∞–¥—å–µ–≤–∏—á", 60, "–ü–Ω–µ–≤–º–æ–Ω–∏—è"),
            new Patient("–°–µ–º–µ–Ω–æ–≤–∞", "–ö—Å–µ–Ω–∏—è", "–ë–æ—Ä–∏—Å–æ–≤–Ω–∞", 25, "–ü–µ—Ä–µ–ª–æ–º –ª—É—á–µ–≤–æ–π –∫–æ—Å—Ç–∏"),
            new Patient("–ï–≥–æ—Ä–æ–≤", "–í–∏–∫—Ç–æ—Ä", "–ö–∏—Ä–∏–ª–ª–æ–≤–∏—á", 55, "–ì—Ä–∏–ø–ø"),
            new Patient("–ü–∞–≤–ª–æ–≤–∞", "–°–≤–µ—Ç–ª–∞–Ω–∞", "–ò–≤–∞–Ω–æ–≤–Ω–∞", 41, "–•—Ä–æ–Ω–∏—á–µ—Å–∫–∏–π —Ç–æ–Ω–∑–∏–ª–ª–∏—Ç"),
            new Patient("–¢–∏—Ö–æ–Ω–æ–≤", "–ì–µ–æ—Ä–≥–∏–π", "–§—ë–¥–æ—Ä–æ–≤–∏—á", 8, "–ö–æ—Ä—å"),
            new Patient("–ì–µ—Ä–∞—Å–∏–º–æ–≤–∞", "–í–µ—Ä–∞", "–ê–Ω–¥—Ä–µ–µ–≤–Ω–∞", 33, "–ü—Ä–æ—Å—Ç—É–¥–∞"),
            new Patient("–ó–∞–π—Ü–µ–≤", "–ù–∏–∫–∏—Ç–∞", "–ú–∏—Ö–∞–π–ª–æ–≤–∏—á", 29, "–†–∞—Å—Ç—è–∂–µ–Ω–∏–µ —Å–≤—è–∑–æ–∫ –≥–æ–ª–µ–Ω–æ—Å—Ç–æ–ø–∞"),
            new Patient("–§—Ä–æ–ª–æ–≤–∞", "–ú–∞—Ä–∏–Ω–∞", "–í–∞–ª–µ–Ω—Ç–∏–Ω–æ–≤–Ω–∞", 58, "–ü—Ä–æ—Å—Ç—É–¥–∞")
        };

        public void ShowPatient()
        {
            foreach (var patient in _patients)
            {
                Console.WriteLine(patient);
            }
        }

        public void SortByFullName()
        {
            _patients = _patients.OrderBy(patient => patient.LastName)
                                 .ThenBy(patient => patient.FirstName)
                                 .ThenBy(patient => patient.Patronymic)
                                 .ToList();
        }

        public void SortByAge()
        {
            _patients = _patients.OrderBy(patients => patients.Age).ToList();
        }

        public void ShowDisease(string disease)
        {
            var filteredPatients = _patients.Where(patients => string.Equals(patients.Disease, disease, StringComparison.OrdinalIgnoreCase)).ToList();

            foreach (var patient in filteredPatients) 
            {
                Console.WriteLine(patient); 
            }
        }
    }

    public class Menu
    {
        private Hospital _hospital;

        public Menu(Hospital hospital)
        {
            _hospital = hospital;
        }

        public void Run()
        {
            const string ShowAllPatientsCommand = "0";
            const string SortByFullNameCommand = "1";
            const string SortByAgeCommand = "2";
            const string ShowDiseaseCommand = "3";
            const string ExitCommand = "4";

            bool isWorking = true;

            while (isWorking)
            {
                Console.Clear();
                Console.WriteLine(">>> –°–ò–°–¢–ï–ú–ê –£–ü–†–ê–í–õ–ï–ù–ò–Ø –ë–û–õ–¨–ù–ò–¶–ï–ô <<<");
                Console.WriteLine($"{ShowAllPatientsCommand}. –ü–æ–∫–∞–∑–∞—Ç—å –≤—Å–µ—Ö –ø–∞—Ü–∏–µ–Ω—Ç–æ–≤");
                Console.WriteLine($"{SortByFullNameCommand}. –°–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å –ø–∞—Ü–∏–µ–Ω—Ç–æ–≤ –ø–æ –§–ò–û");
                Console.WriteLine($"{SortByAgeCommand}. –°–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å –ø–∞—Ü–∏–µ–Ω—Ç–æ–≤ –ø–æ –≤–æ–∑—Ä–∞—Å—Ç—É");
                Console.WriteLine($"{ShowDiseaseCommand}. –ù–∞–π—Ç–∏ –ø–∞—Ü–∏–µ–Ω—Ç–æ–≤ –ø–æ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—é");
                Console.WriteLine($"{ExitCommand}. –í—ã—Ö–æ–¥");
                Console.Write("\n–í–∞—à –≤—ã–±–æ—Ä: ");

                string userInput = Console.ReadLine();

                switch (userInput)
                {
                    case ShowAllPatientsCommand:
                        _hospital.ShowPatient();
                        break;

                    case SortByFullNameCommand:
                        _hospital.SortByFullName();
                        _hospital.ShowPatient();
                        break;

                    case SortByAgeCommand:
                        _hospital.SortByAge();
                        _hospital.ShowPatient();
                        break;

                    case ShowDiseaseCommand:
                        HandleDiseaseSearch();
                        break;

                    case ExitCommand:
                        isWorking = false;
                        Console.WriteLine("–†–∞–±–æ—Ç–∞ –∑–∞–≤–µ—Ä—à–µ–Ω–∞.");
                        break;

                    default:
                        Console.WriteLine("–ù–µ—Ç —Ç–∞–∫–æ–π –∫–æ–º–∞–Ω–¥—ã.");
                        break;
                }

                if (isWorking)
                {
                    Console.WriteLine("\n–ù–∞–∂–º–∏—Ç–µ –ª—é–±—É—é –∫–Ω–æ–ø–∫—É...");
                    Console.ReadKey();
                }
            }
        }

        private void HandleDiseaseSearch()
        {
            Console.Write("–í–≤–µ–¥–∏—Ç–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏–µ: ");
            string disease = Console.ReadLine();

            _hospital.ShowDisease(disease);
        }
    }
}
